let createPost=function(){let t=$("#new-post-form");t.submit((function(e){e.preventDefault(),$.ajax({type:"post",url:"/posts/create",data:t.serialize(),success:function(t){let e=newPostDom(t.data.post);$("#posts-list-container>ul").prepend(e),deletePost($(" .delete-post-button",e)),new PostComments(t.data.post._id),new ToggleLike($(" .toggle-like-button",e)),new Noty({theme:"relax",text:"Post published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(t){console.log(t.responseText)}})}))},newPostDom=function(t){return $(`<li id="post-${t._id}">\n                  <p>\n                      <small>\n                          <a class="delete-post-button" href="/posts/destroy/${t._id}">\n                              X\n                          </a>\n                      </small>\n              \n                      ${t.content}\n                      <br>\n              \n                      <small>\n                          ${t.user.name}\n                      </small>\n                      <br>\n                      <small>\n                          <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${t._id}&type=Post">\n                              0 Likes\n                          </a>\n                      </small>\n                  </p>\n                      <div class="post-comments">\n                          <form action="/comments/create" id="new-comment-form" method="POST">\n                              <input type="text" name="content" placeholder="Type here to add comment..." required>\n                              <input type="hidden" name="post" value="${t._id}">\n                              <input type="submit" value="Add Comment">\n                          </form>\n              \n                          <div class="post-comments-list">\n                              <ul id="post-comments-${t._id}">\n                                  \n                              </ul>\n                          </div>\n              \n                      </div>\n                  \n              </li>`)},deletePost=function(t){$(t).click((function(e){e.preventDefault(),$.ajax({type:"get",url:$(t).prop("href"),success:function(t){$("#post-"+t.data.post_id).remove(),new Noty({theme:"relax",text:"Post Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(t){console.log(t.responseText)}})}))},convertPostsToAjax=function(){$("#posts-list-container>ul>li").each((function(){let t=$(this),e=$(" .delete-post-button",t);deletePost(e);let n=t.prop("id").split("-")[1];new PostComments(n)}))};createPost(),convertPostsToAjax();